"use strict";const mainMenuBtn=document.getElementById("main-menu-btn"),menuOpts=document.getElementById("menu-options");let menuActive=!1;function buildMenu(){mainMenuBtn.classList.add("menu-active");const e=document.createDocumentFragment();for(const n in menuOptions){const t=menuOptions[n];if(!player.prefs.debug&&t.debug);else{const n=document.createElement("button");n.textContent=t.name,n.setAttribute("title",t.description),n.setAttribute("class","pkmnfont"),n.addEventListener("click",(()=>{t.modal?openModal(t.src,t.name):t.custom()})),e.appendChild(n)}}menuOpts.appendChild(e),menuActive=!0}function closeMenu(){disposeElement(menuOpts),mainMenuBtn.classList.remove("menu-active"),menuActive=!1}mainMenuBtn.addEventListener("click",(()=>{menuActive||buildMenu()})),window.onclick=function(e){!e.target.matches("#main-menu-btn")&&menuActive&&closeMenu()};